if ( ! defined( 'ABSPATH' ) ) { exit; } $general = $this->cc_get_settings( 'general' ); if ( isset( $general['show_hub_link'] ) && 'yes' === $general['show_hub_link'] ) { $new_notices['hub_page_in_menu'] = 'PLEASE NOTE: ' . 'The setting "Show HUB Page link in menu" is no longer valid in this latest update. ' . 'You can still add a HUB Page menu link manually if desired, ' . 'from <a target="_blank" href="' . admin_url( 'nav-menus.php' ) . '">HERE</a>'; $this->add_wpc_notices( $new_notices ); unset( $general['show_hub_link'], $general['hub_link_text'] ); do_action( 'wp_client_settings_update', $general, 'general' ); } if ( $this->cc_get_settings( 'notification_moved_menu_settings' ) ) { $new_notices['notification_moved_menu_settings'] = sprintf( 'NOTICE: Custom Navigation settings have been moved to ' . '<a href="%s" target="_blank">Manage Locations</a>', add_query_arg( 'action', 'locations', admin_url( 'nav-menus.php' ) ) ); $this->add_wpc_notices( $new_notices ); $this->cc_delete_settings( 'notification_moved_menu_settings' ); }